import React, { useState, useEffect } from 'react';
import { fetchTasks, addTask as addTaskApi, deleteTask as deleteTaskApi } from '../services/api'; // Adjust the path as needed

const ToDoPage = () => {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');

  // Fetch tasks when the component mounts
  useEffect(() => {
    const getTasks = async () => {
      try {
        const tasksFromApi = await fetchTasks();
        setTasks(tasksFromApi);
      } catch (error) {
        console.error('Failed to fetch tasks:', error);
      }
    };
    getTasks();
  }, []);

  const addTask = async (e) => {
    e.preventDefault();
    if (task.trim()) {
      try {
        const newTask = await addTaskApi(task);
        setTasks([...tasks, newTask]);
        setTask('');
      } catch (error) {
        console.error('Failed to add task:', error);
      }
    }
  };

  const deleteTask = async (taskId) => {
    try {
      await deleteTaskApi(taskId);
      setTasks(tasks.filter(t => t.id !== taskId)); // Adjust to match your task structure
    } catch (error) {
      console.error('Failed to delete task:', error);
    }
  };

  return (
    <div className="container">
      <h2>To-Do List</h2>
      <form onSubmit={addTask}>
        <div className="form-group">
          <input
            type="text"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            className="form-control"
            placeholder="Add a new task"
          />
        </div>
        <button type="submit" className="btn btn-primary">Add Task</button>
      </form>
      <ul className="list-group mt-3">
        {tasks.map((t) => (
          <li key={t.id} className="list-group-item">
            {t.task}
            <button onClick={() => deleteTask(t.id)} className="btn btn-danger btn-sm float-right">Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ToDoPage;
