import React, { useState } from 'react';
import axios from 'axios';

const ToDoPage = () => {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);

  const WEATHER_API_KEY = 'YOUR_OPENWEATHERMAP_API_KEY'; // Replace with your API key

  const fetchWeather = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=London&appid=${WEATHER_API_KEY}&units=metric`);
      setWeather(response.data);
    } catch (error) {
      console.error('Error fetching weather data', error);
    } finally {
      setLoading(false);
    }
  };

  const addTask = (e) => {
    e.preventDefault();
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask('');
      if (task.toLowerCase().includes('outdoor')) {
        fetchWeather();
      }
    }
  };

  const handleInputChange = (e) => {
    setTask(e.target.value);
  };

  return (
    <div className="container">
      <h2>To-Do List</h2>
      <form onSubmit={addTask}>
        <div className="form-group">
          <input
            type="text"
            value={task}
            onChange={handleInputChange}
            className="form-control"
            placeholder="Add a new task"
          />
        </div>
        <button type="submit" className="btn btn-primary">Add Task</button>
      </form>

      <ul className="list-group mt-3">
        {tasks.map((t, index) => (
          <li key={index} className="list-group-item">{t}</li>
        ))}
      </ul>

      {loading && <p>Loading weather...</p>}
      {weather && (
        <div className="weather-info mt-3">
          <h3>Weather Information</h3>
          <p><strong>Location:</strong> {weather.name}</p>
          <p><strong>Temperature:</strong> {weather.main.temp}Â°C</p>
          <p><strong>Weather:</strong> {weather.weather[0].description}</p>
          <p><strong>Humidity:</strong> {weather.main.humidity}%</p>
        </div>
      )}
    </div>
  );
};

export default ToDoPage;
