// src/components/ToDoPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaCalendarAlt, FaBell, FaRedoAlt } from 'react-icons/fa';
import { fetchWeather } from '../services/weatherService';
import ProgressChart from '../components/ProgressChart';
import './ToDoPage.css';

const ToDoPage = () => {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [notification, setNotification] = useState('');
  const [repeat, setRepeat] = useState('None');
  const [dueDate, setDueDate] = useState('');
  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState('New York');
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');
  const navigate = useNavigate();

  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    setTasks(storedTasks);

    const getWeather = async () => {
      try {
        const data = await fetchWeather(location);
        setWeather(data);
      } catch (error) {
        console.error("Error fetching weather data:", error);
      }
    };

    getWeather();
  }, [location]);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  useEffect(() => {
    document.body.className = theme;
    localStorage.setItem('theme', theme);
  }, [theme]);

  const addTask = (e) => {
    e.preventDefault();
    if (task.trim()) {
      setTasks([...tasks, { text: task, priority, dueDate, repeat, notification, completed: false }]);
      setTask('');
      setPriority('Medium');
      setDueDate('');
      setRepeat('None');
      setNotification('');
    }
  };

  const handleInputChange = (e) => {
    setTask(e.target.value);
  };

  const handlePriorityChange = (e) => {
    setPriority(e.target.value);
  };

  const handleNotificationChange = (e) => {
    setNotification(e.target.value);
  };

  const handleRepeatChange = (e) => {
    setRepeat(e.target.value);
  };

  const handleDueDateChange = (e) => {
    setDueDate(e.target.value);
  };

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/');
  };

  const deleteTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  const toggleTaskCompletion = (index) => {
    const updatedTasks = tasks.map((task, i) =>
      i === index ? { ...task, completed: !task.completed } : task
    );
    setTasks(updatedTasks);
  };

  const completedTasks = tasks.filter(task => task.completed).length;
  const totalTasks = tasks.length;

  return (
    <div className={`todo-page ${theme}`}>
      <header>
        <button onClick={() => setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'))}>
          Switch to {theme === 'light' ? 'Dark' : 'Light'} Theme
        </button>
      </header>

      {/* To-Do Section */}
      <div className="todo-section">
        <h2>To-Do List</h2>
        <form onSubmit={addTask}>
          <div className="form-group">
            <input
              type="text"
              value={task}
              onChange={handleInputChange}
              className="form-control"
              placeholder="Add a new task"
              required
            />
          </div>
          <div className="form-group">
            <label>Priority:</label>
            <select value={priority} onChange={handlePriorityChange} className="form-control">
              <option value="High">High</option>
              <option value="Medium">Medium</option>
              <option value="Low">Low</option>
            </select>
          </div>
          <div className="form-group">
            <label>Due Date:</label>
            <input
              type="date"
              value={dueDate}
              onChange={handleDueDateChange}
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label>Notification:</label>
            <input
              type="text"
              value={notification}
              onChange={handleNotificationChange}
              className="form-control"
              placeholder="Notification details"
            />
          </div>
          <div className="form-group">
            <label>Repeat:</label>
            <select value={repeat} onChange={handleRepeatChange} className="form-control">
              <option value="None">None</option>
              <option value="Daily">Daily</option>
              <option value="Weekly">Weekly</option>
              <option value="Monthly">Monthly</option>
            </select>
          </div>
          <button type="submit" className="btn btn-primary">Add Task</button>
        </form>

        {/* Task List */}
        <ul>
          {tasks.map((task, index) => (
            <li key={index} className={`task-item ${task.completed ? 'completed' : ''}`}>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTaskCompletion(index)}
              />
              <span>
                {task.text} - Priority: {task.priority} - Due: {task.dueDate} - Repeat: {task.repeat} - Notify: {task.notification}
              </span>
              <button onClick={() => deleteTask(index)} className="btn btn-danger">Delete</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Weather Section */}
      <div className="weather-container">
        <input
          type="text"
          value={location}
          onChange={handleLocationChange}
          className="form-control"
          placeholder="Enter location"
        />
        {weather && (
          <div>
            <h3>Weather in {weather.location.name}</h3>
            <p>{weather.current.temp_c}°C</p>
            <p>{weather.current.condition.text}</p>
            <img src={weather.current.condition.icon} alt="Weather icon" />
          </div>
        )}
      </div>
    </div>
  );
};

export default ToDoPage;
