import React, { useState, useEffect } from 'react';
import { fetchWeather } from '../services/weatherService';
import { useNavigate } from 'react-router-dom';

const ToDoPage = () => {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState('New York'); // Default location
  const navigate = useNavigate(); // For navigation

  // Load tasks and authentication status from local storage on component mount
  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    const authStatus = localStorage.getItem('authToken');

    if (!authStatus) {
      navigate('/'); // Redirect to home or login if not authenticated
    } else {
      setTasks(storedTasks);
    }
  }, [navigate]);

  // Save tasks to local storage whenever tasks state changes
  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (e) => {
    e.preventDefault();
    if (task.trim()) {
      setTasks([...tasks, { text: task, priority }]);
      setTask('');
      setPriority('Medium'); // Reset priority after adding task
    }
  };

  const handleInputChange = (e) => {
    setTask(e.target.value);
  };

  const handlePriorityChange = (e) => {
    setPriority(e.target.value);
  };

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Clear authentication status
    navigate('/'); // Navigate to home or login page
  };

  const deleteTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  useEffect(() => {
    const getWeather = async () => {
      try {
        const data = await fetchWeather(location);
        setWeather(data);
      } catch (error) {
        console.error("Error fetching weather data:", error);
      }
    };

    getWeather();
  }, [location]);

  return (
    <div className="container">
      <h2>To-Do List</h2>
      <button onClick={handleLogout} className="btn btn-danger mb-3">Logout</button>
      <form onSubmit={addTask}>
        <div className="form-group">
          <input
            type="text"
            value={task}
            onChange={handleInputChange}
            className="form-control"
            placeholder="Add a new task"
          />
        </div>
        <div className="form-group mt-2">
          <label htmlFor="priority">Priority:</label>
          <select
            id="priority"
            value={priority}
            onChange={handlePriorityChange}
            className="form-control"
          >
            <option value="High">High</option>
            <option value="Medium">Medium</option>
            <option value="Low">Low</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary mt-2">Add Task</button>
      </form>

      <div className="mt-4">
        <h3>Current Weather</h3>
        <input
          type="text"
          value={location}
          onChange={handleLocationChange}
          className="form-control"
          placeholder="Enter location"
        />
        {weather ? (
          <div>
            <p><strong>Location:</strong> {weather.location.name}</p>
            <p><strong>Temperature:</strong> {weather.current.temp_c}°C</p>
            <p><strong>Condition:</strong> {weather.current.condition.text}</p>
            <img src={weather.current.condition.icon} alt="Weather icon" />
          </div>
        ) : (
          <p>Loading weather data...</p>
        )}
      </div>

      <ul className="list-group mt-3">
        {tasks.map((t, index) => (
          <li key={index} className={`list-group-item ${t.priority.toLowerCase()}`}>
            {t.text} <span className="badge bg-info float-end">{t.priority}</span>
            <button 
              onClick={() => deleteTask(index)} 
              className="btn btn-danger btn-sm float-end ms-2"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ToDoPage;
