// src/components/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css'; // Import the CSS file for styling

const HomePage = () => {
  const navigate = useNavigate();

  const handleLoginClick = () => {
    navigate('/todo'); // Navigate to To-Do page
  };

  return (
    <div className="container">
      <div className="row m-5 no-gutters shadow-lg">
        <div className="col-md-6 d-none d-md-block">
          <img
            src="https://images.unsplash.com/photo-1518709268806-0d6d091ff23a?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=MnwyNTY5NzF8MHwxfGFsbHwxfHx8fHx8fHx8fDE2NjA2MzEzNjY&ixlib=rb-1.2.1&q=80&w=1080"
            alt="Home Background"
            className="img-fluid"
            style={{ minHeight: '100%' }}
          />
        </div>
        <div className="col-md-6 bg-white p-5 d-flex flex-column justify-content-center align-items-center">
          <h1 className="pb-3">Welcome to To-Do App</h1>
          <div className="d-flex flex-column">
            <button onClick={handleLoginClick} className="btn btn-primary mb-3">
              Login
            </button>
            <button onClick={() => navigate('/signup')} className="btn btn-secondary">
              Sign Up
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
