import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchWeather } from '../services/weatherService';
import ProgressChart from '../components/ProgressChart';
import './ToDoPage.css';

const ToDoPage = () => {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [notification, setNotification] = useState('');
  const [repeat, setRepeat] = useState('None');
  const [dueDate, setDueDate] = useState('');
  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState('New York');
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    setTasks(storedTasks);

    const getWeather = async () => {
      try {
        const data = await fetchWeather(location);
        setWeather(data);
      } catch (error) {
        console.error("Error fetching weather data:", error);
      }
    };

    getWeather();
  }, [location]);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (e) => {
    e.preventDefault();
    if (task.trim()) {
      setTasks([...tasks, { text: task, priority, dueDate, repeat, notification, completed: false }]);
      setTask('');
      setPriority('Medium');
      setDueDate('');
      setRepeat('None');
      setNotification('');
    }
  };

  const toggleTheme = () => {
    setIsDarkTheme(!isDarkTheme);
    document.body.classList.toggle('dark-theme');
  };

  return (
    <div className="container">
      {/* Theme Toggle Button */}
      <button onClick={toggleTheme} className="btn btn-primary">
        Toggle {isDarkTheme ? 'Light' : 'Dark'} Theme
      </button>

      {/* Profile Section */}
      <div className="profile-section">
        <div className="profile-header">
          <h1>Profile</h1>
          <button onClick={() => navigate('/')} className="logout-button">
            Logout
          </button>
        </div>
        <div className="profile-content">
          <img
            src="https://i.pinimg.com/564x/7b/ec/76/7bec76f6638f10d8565913728ec22208.jpg"
            alt="Profile"
            className="profile-img"
          />
          <div className="weather-container">
            <h3>Weather in {location}</h3>
            {weather ? (
              <div>
                <p>{weather.current.condition.text}</p>
                <img src={weather.current.condition.icon} alt={weather.current.condition.text} />
                <p>{weather.current.temp_c}Â°C</p>
              </div>
            ) : (
              <p>Loading weather data...</p>
            )}
            <button onClick={() => setLocation('New Location')}>Change Location</button>
          </div>
          <div className="progress-container">
            <ProgressChart completed={tasks.filter(task => task.completed).length} total={tasks.length} />
          </div>
        </div>
      </div>

      {/* To-Do Section */}
      <div className="todo-section">
        <h2>To-Do List</h2>
        <form onSubmit={addTask}>
          <div className="form-group">
            <input
              type="text"
              value={task}
              onChange={(e) => setTask(e.target.value)}
              className="form-control"
              placeholder="Add a new task"
              required
            />
          </div>
          {/* Rest of the form */}
        </form>

        {/* Task List */}
        <ul>
          {tasks.map((task, index) => (
            <li key={index} className={`task-item ${task.completed ? 'completed' : ''}`}>
              <span>{task.text} - Priority: {task.priority}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ToDoPage;
