import React, { useState, useEffect } from 'react';
import { fetchWeather } from '../services/weatherService';
import { useNavigate } from 'react-router-dom';

const ToDoPage = () => {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');
  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState('New York'); // Default location
  const navigate = useNavigate(); // For navigation

  const addTask = (e) => {
    e.preventDefault();
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask('');
    }
  };

  const handleInputChange = (e) => {
    setTask(e.target.value);
  };

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  const handleLogout = () => {
    // Clear authentication status and navigate to the home page or login page
    // Assuming you handle authentication status with local storage or context
    localStorage.removeItem('authToken'); // Example of clearing auth token
    navigate('/'); // Navigate to home page or login page
  };

  const deleteTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  useEffect(() => {
    const getWeather = async () => {
      try {
        const data = await fetchWeather(location);
        setWeather(data);
      } catch (error) {
        console.error("Error fetching weather data:", error);
      }
    };

    getWeather();
  }, [location]);

  return (
    <div className="container">
      <h2>To-Do List</h2>
      <button onClick={handleLogout} className="btn btn-danger mb-3">Logout</button>
      <form onSubmit={addTask}>
        <div className="form-group">
          <input
            type="text"
            value={task}
            onChange={handleInputChange}
            className="form-control"
            placeholder="Add a new task"
          />
        </div>
        <button type="submit" className="btn btn-primary">Add Task</button>
      </form>

      <div className="mt-4">
        <h3>Current Weather</h3>
        <input
          type="text"
          value={location}
          onChange={handleLocationChange}
          className="form-control"
          placeholder="Enter location"
        />
        {weather ? (
          <div>
            <p><strong>Location:</strong> {weather.location.name}</p>
            <p><strong>Temperature:</strong> {weather.current.temp_c}Â°C</p>
            <p><strong>Condition:</strong> {weather.current.condition.text}</p>
            <img src={weather.current.condition.icon} alt="Weather icon" />
          </div>
        ) : (
          <p>Loading weather data...</p>
        )}
      </div>

      <ul className="list-group mt-3">
        {tasks.map((t, index) => (
          <li key={index} className="list-group-item">
            {t}
            <button 
              onClick={() => deleteTask(index)} 
              className="btn btn-danger btn-sm float-end ms-2"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ToDoPage;
