import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaCalendarAlt, FaBell, FaRedoAlt } from 'react-icons/fa';
import { fetchWeather } from '../services/weatherService';
import './ToDoPage.css'; // Import the CSS file

const ToDoPage = () => {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [notification, setNotification] = useState('');
  const [repeat, setRepeat] = useState('None');
  const [dueDate, setDueDate] = useState('');
  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState('New York');
  const [profileOpen, setProfileOpen] = useState(true); // To toggle profile section visibility
  const navigate = useNavigate();

  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    setTasks(storedTasks);

    const getWeather = async () => {
      try {
        const data = await fetchWeather(location);
        setWeather(data);
      } catch (error) {
        console.error("Error fetching weather data:", error);
      }
    };

    getWeather();
  }, [location]);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (e) => {
    e.preventDefault();
    if (task.trim()) {
      setTasks([...tasks, { text: task, priority, dueDate, repeat, notification }]);
      setTask('');
      setPriority('Medium');
      setDueDate('');
      setRepeat('None');
      setNotification('');
    }
  };

  const handleInputChange = (e) => {
    setTask(e.target.value);
  };

  const handlePriorityChange = (e) => {
    setPriority(e.target.value);
  };

  const handleNotificationChange = (e) => {
    setNotification(e.target.value);
  };

  const handleRepeatChange = (e) => {
    setRepeat(e.target.value);
  };

  const handleDueDateChange = (e) => {
    setDueDate(e.target.value);
  };

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/');
  };

  const deleteTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  return (
    <div className="layout">
      {profileOpen && (
        <div className="profile-section">
          <div className="profile-header">
            <span className="close-button" onClick={() => setProfileOpen(false)}>Close</span>
          </div>
          <div className="profile-content">
            <img
              src="https://i.pinimg.com/564x/50/6c/07/506c075f3f67ad0cbb43c8d35fffd3b8.jpg"
              alt="Profile"
              className="profile-img"
            />
            <h3>Hey, ABCD</h3>

            {/* Weather Feature */}
            <div className="weather-container">
              <h3>Current Weather</h3>
              <input
                type="text"
                value={location}
                onChange={handleLocationChange}
                className="form-control"
                placeholder="Enter location"
              />
              {weather ? (
                <div>
                  <p><strong>Location:</strong> {weather.location.name}</p>
                  <p><strong>Temperature:</strong> {weather.current.temp_c}°C</p>
                  <p><strong>Condition:</strong> {weather.current.condition.text}</p>
                  <img src={weather.current.condition.icon} alt="Weather icon" />
                </div>
              ) : (
                <p>Loading weather data...</p>
              )}
            </div>

            {/* Logout Button */}
            <button onClick={handleLogout} className="btn btn-danger logout-button">Logout</button>
          </div>
        </div>
      )}

      <div className="todo-section">
        <h2>To-Do List</h2>
        <form onSubmit={addTask}>
          <div className="form-group">
            <input
              type="text"
              value={task}
              onChange={handleInputChange}
              className="form-control"
              placeholder="Add a new task"
            />
          </div>
          <div className="form-group">
            <label htmlFor="priority">Priority:</label>
            <select
              id="priority"
              value={priority}
              onChange={handlePriorityChange}
              className="form-control"
            >
              <option value="High">High</option>
              <option value="Medium">Medium</option>
              <option value="Low">Low</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="dueDate">
              <FaCalendarAlt /> Due Date:
            </label>
            <input
              type="date"
              id="dueDate"
              value={dueDate}
              onChange={handleDueDateChange}
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="repeat">
              <FaRedoAlt /> Repeat:
            </label>
            <select
              id="repeat"
              value={repeat}
              onChange={handleRepeatChange}
              className="form-control"
            >
              <option value="None">None</option>
              <option value="Daily">Daily</option>
              <option value="Weekly">Weekly</option>
              <option value="Monthly">Monthly</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="notification">
              <FaBell /> Notification:
            </label>
            <input
              type="text"
              id="notification"
              value={notification}
              onChange={handleNotificationChange}
              className="form-control"
              placeholder="Enter notification message"
            />
          </div>
          <button type="submit" className="btn btn-primary">Add Task</button>
        </form>

        <ul className="list-group mt-3">
          {tasks.map((task, index) => (
            <li key={index} className="list-group-item">
              <span className={`badge bg-${task.priority === 'High' ? 'danger' : task.priority === 'Medium' ? 'warning' : 'success'} float-end`}>
                {task.priority}
              </span>
              <div>{task.text}</div>
              {task.dueDate && <div><FaCalendarAlt /> <strong>Due Date:</strong> {task.dueDate}</div>}
              {task.repeat && <div><FaRedoAlt /> <strong>Repeat:</strong> {task.repeat}</div>}
              {task.notification && <div><FaBell /> <strong>Notification:</strong> {task.notification}</div>}
              <button
                onClick={() => deleteTask(index)}
                className="btn btn-danger btn-sm float-end ms-2"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ToDoPage;
