// src/components/LoginPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { login } from '../redux/authSlice';
import './LoginPage.css'; // Import the CSS file for styling

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleLogin = (e) => {
    e.preventDefault();
    // Dispatch login action
    dispatch(login({ username, password }));

    // Redirect to To-Do page
    navigate('/todo');
  };

  return (
    <div className="container">
      <div className="row m-5 no-gutters shadow-lg">
        <div className="col-md-6 d-none d-md-block">
          <img
            src="https://images.unsplash.com/photo-1566888596782-c7f41cc184c5?ixlib=rb-1.2.1&auto=format&fit=crop&w=2134&q=80"
            alt="Login Background"
            className="img-fluid"
            style={{ minHeight: '100%' }}
          />
        </div>
        <div className="col-md-6 bg-white p-5">
          <h3 className="pb-3">Login Form</h3>
          <form onSubmit={handleLogin}>
            <div className="form-style">
              <div className="form-group pb-3">
                <input
                  type="email"
                  placeholder="Email"
                  className="form-control"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group pb-3">
                <input
                  type="password"
                  placeholder="Password"
                  className="form-control"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <div className="d-flex align-items-center justify-content-between">
                <div className="d-flex align-items-center">
                  <input name="" type="checkbox" value="" />{' '}
                  <span className="pl-2 font-weight-bold">Remember Me</span>
                </div>
                <div>
                  <a href="#">Forget Password?</a>
                </div>
              </div>
              <div className="pb-2">
                <button type="submit" className="btn btn-dark w-100 font-weight-bold mt-2">
                  Submit
                </button>
              </div>
            </div>
            <div className="sideline">OR</div>
            <div>
              <button type="button" className="btn btn-primary w-100 font-weight-bold mt-2">
                <i className="fa fa-facebook" aria-hidden="true"></i> Login With Facebook
              </button>
            </div>
            <div className="pt-4 text-center">
              Get Members Benefit. <a href="/signup">Sign Up</a>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
